import React, { useState, useEffect} from 'react';
import {Redirect} from "react-router-dom";
import Navig from './Navig';

export default function Explore(){
    const [display, setDisplay] = useState(false);
    const [zoom, setZoom] = useState(false);
    const [redir, setRedir] = useState('');

    useEffect(() => { 
      console.log(redir);
    }, [redir]);


    var div = {
      width: "320px",
      height: '250px',
      position: 'absolute',
      left: '570px',
      top: '400px',
      cursor: 'pointer'
    }
    if(!display){
    var disp = ''
  }else{
      var disp = <img style={{position:'absolute',left: '570px',top: '400px'}} src='https://media.giphy.com/media/OP9DVYIfo6hu8/source.gif'></img>
    }
    if(!zoom){
      var zoomin = {}
    }else{
        zoomin = {
          webkitTransitionDuration: '2.5s',
          webkitTransform: 'scale(3.5)',
          transform: 'scale(3.5)'
        }
    }

    return (
      <div style={zoomin} className="background-exp">
        <Navig />
        {disp}
        <div title="Jupiter" id="jupiter" style={div} onClick={() =>{setZoom(!zoom); setTimeout(function(){ setDisplay(!display); },1000); setTimeout(function(){ setRedir(<Redirect to="/jupiter" />) },3200)}}></div>
        {redir}
      </div>
    )
  }
