import React, { useState, useEffect } from 'react';
import '../App.css';
import {
    Button,InputGroup, InputGroupAddon, InputGroupText, Input, Jumbotron
  } from 'reactstrap';
import Navig from './Navig';
import jupiter from '../img/jupiter.jpg';
import {connect} from 'react-redux';

function Map(props) {
  const [tripList, setTripList] = useState([]);

    useEffect(()=>{
      console.log(props.trip)
      const Search = async() => {
        var rawResponse = await fetch(`https://launchlibrary.net/1.4/launch/${props.trip.date1}/${props.trip.date2}`);
          var response = await rawResponse.json();
          console.log(response);
          if(response !== undefined){
            console.log('COUNTRY',response.launches[0].location.name, 'AGENCY', response.launches[0].lsp.name, 'DATE', response.launches[0].net, 'ROCKET', response.launches[0].rocket.name );
            setTripList(response.launches);
          }
       }
      Search();
    },[]);

    useEffect(()=>{
      console.log(tripList)
    }, [tripList])

    var button = {backgroundColor: '#70A19D' , borderColor: '#8FD0CE', borderWidth: '2px'};
    var bold = {fontWeight: 'bold'};

    return (
        <div style={{backgroundColor: 'black'}}>
          <div className='background-map'>
            <Navig/>
          </div>

          <h1 className='display-4 text-center' style={{padding: '20px', color: 'white'}}>Choose Your Rocket</h1>
          <div style={{backgroundColor: '#70A19D'}}>
            <div className='d-flex justify-content-between' style={{width: '70%', padding: '15px', margin: 'auto'}}>
              <div className='d-flex justify-content-between' style={{width: '40%', fontSize: '20px'}}>
                <span>From: {props.trip.location}</span>
                <span>To: {props.trip.destination}</span>
              </div>
              <div className='d-flex justify-content-between' style={{width: '35%', fontSize: '20px'}}>
                <span>From: {props.trip.date1}</span>
                <span>To: {props.trip.date2}</span>
              </div>
              <Button style={{backgroundColor: '#54757E', borderColor: '#4C6A73', borderWidth: '2px'}}>Modify</Button>
            </div>
          </div>
          <div style={{backgroundColor: '#54757E'}}>
            <div className='d-flex justify-content-between' style={{width: '70%', fontSize: '20px',  padding: '15px', margin: 'auto'}}>
              <Button style={button}>Filtre 1</Button>
              <Button style={button}>Filtre 2</Button>
              <Button style={button}>Filtre 3</Button>
              <Button style={button}>Filtre 4</Button>
            </div>
          </div>
          <MapContainer center={[51.505, -0.09]} zoom={13} scrollWheelZoom={false}>
            <TileLayer
              attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <Marker position={[51.505, -0.09]}>
              <Popup>
                A pretty CSS3 popup. <br /> Easily customizable.
              </Popup>
            </Marker>
          </MapContainer>
          <div className='background-map2'>
            {tripList.map(function(el, i){
          return <Jumbotron key={i} className='jumbo'>
            <div className='row' style={{marginTop: '-1cm', marginBottom: '0.8cm'}}>
              <img src={jupiter} className='col-4' style={{borderRadius: '30px'}}/>
              <div className='col-8'>
                <p className="lead"><span style={bold}>City of departure:</span> {el.location.name}</p>
                <p className="lead"><span style={bold}>Date of departure:</span> {el.net}</p>
                <p className="lead"><span style={bold}>Rocket name:</span> {el.rocket.name}</p>
                <p className="lead"><span style={bold}>Agency:</span> {el.lsp.name}</p>
              </div>
            </div>
            <hr className="my-2" />
            <Button color="primary" style={{marginBottom: '-1cm'}}>Book Now</Button>
          </Jumbotron>
          })
          }
          </div>

        </div>
    )
}

function mapStateToProps(state) {
  return { trip: state.trip }
}

export default connect(
  mapStateToProps, 
  null
)(Map);
