import React, { useState, useEffect } from 'react';
import '../App.css';
import {
    Button,InputGroup, InputGroupAddon, InputGroupText, Input, Jumbotron, Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Modal, ModalHeader, ModalBody, ModalFooter 
  } from 'reactstrap';
import Navig from './Navig';
import {Link, Redirect} from "react-router-dom";

import {connect} from 'react-redux';


function Trips(props) {
    const [redir, setRedir] = useState(<div/>);
    const [trips, setTrips] = useState([]);

    var bold = {fontWeight: 'bold'};

    useEffect(()=>{
        async function loadTrips(){
            if(props.token){
                const data = await fetch('/trips', {
                    method: 'POST',
                    headers: {'Content-Type': 'application/x-www-form-urlencoded'},
                    body: `token=${props.token}`
                  })
                  const body = await data.json()
                  console.log(body);
                  setTrips(body.trips);
            }
        }
        loadTrips()
    },[])

    if(trips.length === 0){
        var allTrips= <p style={{color: 'white'}}>No Trips</p>
    }else{
    var allTrips = trips.map(el=>{
        return <div style={{padding:10, display:'flex', flexDirection:'row wrap', justifyContent:'space-between'}}>
         <p className="lead"><span style={bold}>City of departure:</span> {el.city}</p>
         <p className="lead"><span style={bold}>Destination:</span> {el.destination}</p>
        <p className="lead"><span style={bold}>Date of departure:</span> {el.departure}</p>
        <p className="lead"><span style={bold}>Rocket name:</span> {el.rocket}</p>
        <p className="lead"><span style={bold}>Agency:</span> {el.agency}</p>
        <p className="lead"><span style={bold}>Price:</span> {el.price}€</p>
    </div>
    })
}

    return (
        <div className="backgroundTrip">
          <Navig />
          <div className="container">
            <div className="flex">
              <div className="row">
                  <h1 className='display-4' style={{color: 'white', marginBottom: 40}}>My Trips</h1>
                  <Link style={{marginLeft: 10, marginTop:-40, color:'#d9534f'}} onClick={()=>{props.addToken(''); setRedir(<Redirect to='/'/>)}}>Log Out</Link>
             </div>
             {allTrips}
             {redir}
            </div>
          </div>
        </div>
      );

}

function mapDispatchToProps(dispatch){
    return {
      addToken: function(token){
        dispatch({type: 'token', token: token})
      }
    }
  }

  function mapStateToProps(state){
    return {token: state.token}
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Trips);
