import React, { useState} from 'react';
import './App.css';
import {
  BrowserRouter as Router,Switch,Route
} from "react-router-dom";
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';

import Home from './Home';
import Jupiter from './Jupiter';
import Explore from './Explore';
import Navig from './Navig';

function App(){
  return (
    <Router>
      <div>
        <Switch>
            <Route path="/explore" exact component={Explore} />
            <Route path="/jupiter" exact component={Jupiter}/>
            <Route path="/discover">
              <Discover/>
            </Route>
            <Route path="/" exact component={Home} />
        </Switch>
      </div>
    </Router>
  );
}

function Discover(){
  const items = [
    {
      id: 1,
      altText: 'Slide 1',
      caption: 'Slide 1'
    },
    {
      id: 2,
      altText: 'Slide 2',
      caption: 'Slide 2'
    },
    {
      id: 3,
      altText: 'Slide 3',
      caption: 'Slide 3'
    }
  ];

  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  }

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  }

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  }
  var background;

  const slides = items.map((item) => {
    if(item.id==1){
      var background = "custom-tag-1"
    }else if(item.id==2){
      var background = "custom-tag-2"
    }else{
      var background = "custom-tag-3"
    }
    return (
      <CarouselItem
        className={background}
        tag="div"
        key={item.id}
        onExiting={() => setAnimating(true)}
        onExited={() => setAnimating(false)}

      >
        <CarouselCaption className="text-danger" captionText={item.caption} captionHeader='bla bla' />
      </CarouselItem>
    );
  });

  return (
    <div className="background-disc">
      <Navig/>
      <div>
      {/* <style>
        {
          `.custom-tag {
              max-width: 100%;
              background-image: url(${background});
              height: 90vh;
              background-size: cover;
            }`
        }
      </style> */}
      <Carousel
        activeIndex={activeIndex}
        next={next}
        previous={previous}
      >
        <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={goToIndex} />
        {slides}
        <CarouselControl direction="prev" directionText="Previous" onClickHandler={previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={next} />
      </Carousel>
    </div>
    </div>
  )
}

export default App;
