import React, { useState, useEffect } from 'react';
import '../App.css';
import {
    Button,InputGroup, InputGroupAddon, InputGroupText, Input, Jumbotron, Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Modal, ModalHeader, ModalBody, ModalFooter 
  } from 'reactstrap';
import Navig from './Navig';
import {Link, Redirect} from "react-router-dom";

import {connect} from 'react-redux';


function Trips(props) {
    const [redir, setRedir] = useState(<div/>);
    const [trips, setTrips] = useState([]);

    useEffect(()=>{
        async function loadTrips(){
            if(props.token){
                const data = await fetch('/trips', {
                    method: 'POST',
                    headers: {'Content-Type': 'application/x-www-form-urlencoded'},
                    body: `token=${props.token}`
                  })
                  const body = await data.json()
                  console.log(body);
                  setTrips(body.trips);
            }
        }
        loadTrips()
    },[])

    return (
        <div className="backgroundTrip">
          <Navig />
          <div className="container">
            <div className="flex">
              <div className="row">
                  <h1 className='display-4' style={{color: 'white'}}>My Trips</h1>
                  <Link style={{marginLeft: 10, marginTop:-40, color:'#d9534f'}} onClick={()=>{props.addToken(''); setRedir(<Redirect to='/'/>)}}>Log Out</Link>
             </div>
             {trips.map(el=>{
                 return <div className="row" style={{padding:10}}>
                  <p style={{color: 'white'}}>Name: {el.rocket} </p>
                  <p style={{color: 'white'}}>Price: {el.price}â‚¬ </p>
             </div>
             })}
             {redir}
            </div>
          </div>
        </div>
      );

}

function mapDispatchToProps(dispatch){
    return {
      addToken: function(token){
        dispatch({type: 'token', token: token})
      }
    }
  }

  function mapStateToProps(state){
    return {token: state.token}
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Trips);
