import React, { useState, useEffect } from 'react';
import '../App.css';
import {
    Button,InputGroup, InputGroupAddon, InputGroupText, Input, Jumbotron, Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Modal, ModalHeader, ModalBody, ModalFooter 
  } from 'reactstrap';
import Navig from './Navig';

import {connect} from 'react-redux';
import { Redirect } from 'react-router-dom';


function Sign(props) {
  const [signUpUsername, setSignUpUsername] = useState('');
  const [signUpEmail, setSignUpEmail] = useState('');
  const [signUpPassword, setSignUpPassword] = useState('');
  const [listErrorSignUp, setListErrorSignUp] = useState([]);
  const [redir, setRedir] = useState(<div/>);
  const [signup, setSignup] = useState(false);


  var handleSubmitSignUp = async () => {

    const data = await fetch('/sign-up', {
      method: 'POST',
      headers: {'Content-Type': 'application/x-www-form-urlencoded'},
      body: `username=${signUpUsername}&email=${signUpEmail}&password=${signUpPassword}`
    })

    const body = await data.json()

    if(body.result == true){
      console.log('Logged in!');
      props.addToken(body.token);
      setRedir(<Redirect to='/trips'/>);

    } else {
      setListErrorSignUp(body.error)
    }
  }

  var tabErrorsSignup = listErrorSignUp.map((error,i) => {
    return(<div style={{color: 'red', fontSize: 12}}>{error}</div>)
  })

  if(signup){
  var username = <InputGroup style={{marginLeft:"15px", width: "300px",marginBottom:"15px"}}>
  <InputGroupAddon addonType="prepend">
    <InputGroupText>U</InputGroupText>
  </InputGroupAddon>
  <Input placeholder="Username" onChange={(e)=>setSignUpUsername(e.target.value)} value={signUpUsername} />
</InputGroup>
  var noAccount = <div style={{display: 'flex', flexDirection:'row',alignItems:'baseline', marginBottom: '-1cm'}}> <Button color="primary" style={{flex:1}} onClick={()=>handleSubmitSignUp()}>Sign up</Button>
  <p style={{fontSize:12, cursor: 'pointer', color:'#0275d8', marginLeft: 20, flex:2.5}} onClick={()=>setSignup(false)}> If you already have an account Sign in</p></div>

  }else{
    var username= <div/>
    var noAccount = <div style={{display: 'flex', flexDirection:'row', alignItems:'baseline', marginBottom: '-1cm'}}> <Button color="primary" style={{flex:0.5}} onClick={()=>handleSubmitSignUp()}>Log In</Button>
    <p style={{fontSize:12, cursor: 'pointer', color:'#0275d8', marginLeft: 20, flex:2}} onClick={()=>setSignup(true)}> If you don't already have an account Sign up</p> </div>
  }

    return (
        <div className="backgroundSign">
          <Navig />
          <div className="container">
            <div className="flex">
              <div className="row">
              <Jumbotron className='jumbo'>
            <div className='row' style={{marginTop: '-1cm', marginBottom: '0.8cm'}}>
              <div className='col-8'>
              {username}
              <InputGroup style={{marginLeft:"15px", width: "300px",marginBottom:"15px"}}>
              <InputGroupAddon addonType="prepend">
                <InputGroupText>@</InputGroupText>
              </InputGroupAddon>
              <Input placeholder="Email" onChange={(e)=>setSignUpEmail(e.target.value)} value={signUpEmail} />
            </InputGroup>
            <InputGroup style={{marginLeft:"15px", width: "300px",marginBottom:"15px"}}>
              <InputGroupAddon addonType="prepend">
                <InputGroupText>**</InputGroupText>
              </InputGroupAddon>
              <Input placeholder="Password" onChange={(e)=>setSignUpPassword(e.target.value)} value={signUpPassword} />
            </InputGroup>
            {tabErrorsSignup}
              </div>
            </div>
            <hr className="my-2" />
            {noAccount}
              </Jumbotron>
             </div>
              {redir}
            </div>
          </div>
        </div>
      );

}

function mapDispatchToProps(dispatch){
  return {
    addToken: function(token){
      dispatch({type: 'token', token: token})
    }
  }
}

export default connect(
  null,
  mapDispatchToProps
)(Sign);
