import React, { useState} from 'react';
import {
    BrowserRouter as Redirect
  } from "react-router-dom";
import Navig from './Navig';

export default function Explore(){
    const [display, setDisplay] = useState(false);
    const [zoom, setZoom] = useState(false);
    const [redir, setRedir] = useState('');

    var div = {
      width: "200px",
      height: '200px',
      position: 'absolute',
      left: '470px',
      top: '160px',
      cursor: 'pointer'
    }
    if(!display){
    var disp = {
      display: 'none'
    }}else{
      var disp = {
        position: 'absolute',
        left: '23.25%',
        top: '18.5%'
      }
    }
    if(!zoom){
      var zoomin = {}
    }else{
        zoomin = {
          webkitTransitionDuration: '2.5s',
          webkitTransform: 'scale(3.5)',
          transform: 'scale(3.5)'
        }
    }

    return (
      <div style={zoomin} className="background-exp">
        <Navig />
        <img style={disp} src='https://media.giphy.com/media/OP9DVYIfo6hu8/source.gif'></img>
        <div title="Jupiter" id="jupiter" style={div} onClick={() =>{setZoom(!zoom); setTimeout(function(){ setDisplay(!display); },1000); setTimeout(function(){ setRedir(<Redirect to="/jupiter" />); },3700)}}></div>
        {redir}
      </div>
    )
  }
