import React, { useState } from 'react';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem, Button,
  NavLink,InputGroup, InputGroupAddon, InputGroupText, Input, 
  Popover, PopoverHeader, PopoverBody
} from 'reactstrap';

function App(){
  return (
    <Router>
      <div>
        <Switch>
            <Route path="/exp">
              <Page2 />
            </Route>
            <Route path="/">
              <Home/>
            </Route>
        </Switch>
      </div>
    </Router>
  );
}

function Navig(){
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);
  var logo = {
    width: "180px"
  };
  return (
        <Navbar expand="md">
          <NavbarBrand><Link to="/"><img src="/rockarocket-logo-white.png" style={logo}/></Link></NavbarBrand>
            <Nav className="mr-auto" navbar>
            </Nav>
            <NavLink><Link to="/exp">Explore</Link></NavLink>
            <NavLink href="#">Get Inspired</NavLink>
            <NavLink href="#">Log In</NavLink>
        </Navbar>
  )
}

function Home() {
  var white = {
    color: "white"
  }
  var width = {
    width: "300px"
  }
  var margin = {
    marginLeft:"10px"
  }
  var from = {
    marginBottom:"10px",
    width: "300px"
  }
  var time = {
    marginBottom:"10px",
    marginLeft:"10px"
  }
  var min = {
    minWidth: '600px'
  }
  return (
    <div className="background">
      <Navig />
      <div className="container">
        <div className="flex">
          <div className="row">
            <h1 className="display-3 text-center" style={white}>FIND A TRIP</h1>
          </div>
          <div className="row">
            <InputGroup style={from}>
              <InputGroupAddon addonType="prepend">
                <InputGroupText>From</InputGroupText>
              </InputGroupAddon>
              <Input placeholder="Your Location" />
            </InputGroup>
            <input style={time} type="time" ></input>
          </div>
          <div className="row" style={min}>
            <InputGroup style={width}>
                <InputGroupAddon addonType="prepend">
                  <InputGroupText>To</InputGroupText>
                </InputGroupAddon>
                <Input placeholder="Destination" />
            </InputGroup>
            <input style={margin} type="date" ></input>
            <Button style={margin}>Search</Button>
          </div>
        </div>
      </div>
    </div>
  );
}

var Page2 = () => {
  const [popoverOpen, setPopoverOpen] = useState(false);
  const [popoverOpen2, setPopoverOpen2] = useState(false);
  const [display, setDisplay] = useState(false);
  const [zoom, setZoom] = useState(false);
  const toggle = () => setPopoverOpen(!popoverOpen);
  const toggle2 = () => setPopoverOpen2(!popoverOpen2);
  var widthImg = {
    width: "100vw"
  }

  if(!display){
  var disp = {
    display: 'none'
  }}else{
    var disp = {
      position: 'absolute',
      left: '0',
      top: '0'
    }
  }
  if(!zoom){
    var div = {
      width: "200px",
      height: '200px',
      position: 'absolute',
      left: '470px',
      top: '160px',
      cursor: 'pointer'
    }
  }else{
    var div = {
      width: "200px",
      height: '200px',
      position: 'absolute',
      left: '470px',
      top: '160px',
      cursor: 'pointer',
      zoom: '300%'
    }
  }
  return (
    <div className="background-exp">
      <Navig />
      <img style={disp} src='https://media.giphy.com/media/OP9DVYIfo6hu8/source.gif'></img>
      {/* <img src="https://www.washingtonpost.com/rf/image_1484w/2010-2019/WashingtonPost/2016/05/13/KidsPost/Images/05298540.jpg?t=20170517" usemap="#image-map" style={widthImg}></img> */}
      <div id="jupiter" style={div} onClick={() =>{setDisplay(!display); setZoom(!zoom)}}></div>
      {/* <map name="image-map">
        <area alt="earth" title="earth" id="earth" coords="1394,891,243" shape="circle" type="button"/>
        <area alt="jupiter" title="jupiter" id="jupiter" coords="591,270,117" shape="circle" type="button"></area>
      </map> */}
      {/* <Popover id="pop1" isOpen={popoverOpen} target="earth" toggle={toggle}>
        <PopoverHeader>Earth</PopoverHeader>
        <PopoverBody>Sed posuere consectetur est at lobortis. Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum.</PopoverBody>
      </Popover>
      <Popover id="pop2" isOpen={popoverOpen2} target="jupiter" toggle={toggle2}>
        <PopoverHeader>Jupiter</PopoverHeader>
        <PopoverBody>Sed posuere consectetur est at lobortis. Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum.</PopoverBody>
      </Popover> */}
    </div>
  )
}

export default App;
