import React, { useState, useEffect } from 'react';
import {
    Button,InputGroup, InputGroupAddon, InputGroupText, Input,
  } from 'reactstrap';
import Navig from './Navig';
import {Link} from "react-router-dom";
import {connect} from 'react-redux';

function Home(props) {
  const [location, setLocation]= useState('');
  const [date1, setDate1]= useState('');
  const [date2, setDate2]= useState('');
  const [destination, setDestination]= useState('');
  const [lat, setLat]= useState(0);
  const [lon, setLon]= useState(0);
  const [uncomplete, setUncomplete]= useState(false);

  useEffect(()=>{
    if('geolocation' in navigator) {
        console.log('geolocation is available');
      } else {
        console.log('geolocation IS NOT available');
      }
    navigator.geolocation.getCurrentPosition(async(position) => {
      console.log(position.coords.latitude, position.coords.longitude);
      await setLat(position.coords.latitude);
      await setLon(position.coords.longitude);
    });
    const API = async()=> {
      var rawResponse = await fetch(`https://forward-reverse-geocoding.p.rapidapi.com/v1/reverse?format=json&lon=${lon}&lat=${lat}&accept-language=en&polygon_threshold=0.0`, {
      "method": "GET",
      "headers": {
          "x-rapidapi-key": "eb71094070msh0f063c4534649bcp14ec8djsn9f81e1fd2c7b",
          "x-rapidapi-host": "forward-reverse-geocoding.p.rapidapi.com"
      }
      });
      var response = await rawResponse.json();
      console.log(response);
      setLocation(response.address.city+', '+response.address.country)
    }
    if(lon !== 0 && lat !== 0){
    API();
    }
  });

  var alert = <div></div>

  if(location!==''||destination!==''||date1!==''||date2!==''){
    var button = <Link to='/map'><Button style={{marginLeft:"15px",marginBottom:"15px"}} onClick={()=>props.SearchTrip(location, destination, date1, date2)}>Search</Button></Link>
  }else{
    var button = <Button style={{marginLeft:"15px",marginBottom:"15px"}} onClick={()=> setUncomplete(true)}>Search</Button>
  }

  if(uncomplete){
    var alert = <div className="row"><p style={{color:'red', backgroundColor: '#f7c7c7', paddingLeft: '0.5cm', paddingRight: '0.5cm', paddingTop: '0.2cm', paddingBottom: '0.2cm', borderRadius: '20px'}}>Please fill all the input fields</p></div>
  }

  return (
    <div className="background">
      <Navig />
      <div className="container">
        <div className="flex">
          <div className="row">
            <h1 className="display-3 text-center" style={{color: "white"}}>FIND A TRIP</h1>
          </div>
          <div className="row">
            <InputGroup style={{marginBottom:"15px",width: "450px"}}>
              <InputGroupAddon addonType="prepend">
                <InputGroupText>In Between</InputGroupText>
              </InputGroupAddon>
              <Input type="date" onChange={(e)=>setDate1(e.target.value)} value={date1}/>
              <Input type="date" onChange={(e)=>setDate2(e.target.value)} value={date2}/>
            </InputGroup>
          </div>
          <div className="row">
          <InputGroup style={{marginLeft:"15px", width: "300px",marginBottom:"15px"}}>
              <InputGroupAddon addonType="prepend">
                <InputGroupText>From</InputGroupText>
              </InputGroupAddon>
              <Input placeholder="Your Location" onChange={(e)=>setLocation(e.target.value)} value={location}/>
            </InputGroup>
            <InputGroup style={{marginLeft:"15px",width: "300px", marginBottom:"15px"}}>
                <InputGroupAddon addonType="prepend">
                  <InputGroupText>To</InputGroupText>
                </InputGroupAddon>
                <Input placeholder="Destination" onChange={(e)=>setDestination(e.target.value)} value={destination}/>
            </InputGroup>
            {button}
          </div>
          {alert}
        </div>
      </div>
    </div>
  );
}

function mapDispatchToProps(dispatch) {
  return {
    SearchTrip: function(location, destination, date1, date2) { 
        dispatch( {type: 'trip', tripInfo:{location: location, ldestination: destination, date1: date1, date2: date2}} ) 
    }
  }
}

export default connect(
    null, 
    mapDispatchToProps
)(Home);
